      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];

  export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    return(
        <>
            <div className="app">
                <div className="sidebar">
                    <FrdsList frds={frds}></FrdsList>
                </div>
            </div>
        </>
    )
}

const FrdsList = ({frds}) => {
    return(
        <ul>
            {
                frds.map((frd) => (
                    <Frds frd={frd}></Frds>
                ))
            }
        </ul>
    )
}

const Frds = ({frd}) => {
    return(
        <>
            <li key={frd.id}>
                <img src={frd.image} alt={frd.name} />
                <h3>{frd.name}</h3>
                <p>{frd.balance}</p>
                <button>Select</button>
            </li>
        </>
    )
}

const AddFrd = () => {
