import React,{useState} from "react";
const initial = [
    {
      id: 118836,
      name: "Clark",
      image: "https://i.pravatar.cc/48?u=118836",
      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];


export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    const [showFrd,setShowFrd] = useState(false);

    const handleAddFrd = () => {
        setShowFrd(!showFrd);
    }
    const handleAdd = (frd) => {
        setFrds()
    }
    return(
        <>
            <div className="app">
                <div className="sidebar">
                    <FrdsList frds={frds}></FrdsList>
                    <Button onClick={handleAddFrd}>Add Friend</Button>
                    {showFrd && <AddFrd></AddFrd>}
                </div>
                <SplitBill></SplitBill>
            </div>
        </>
    )
}
const Button = ({children,onClick}) => {
    return(
       <button className="button" onClick={onClick}>{children}</button>
    )
}
const FrdsList = ({frds}) => {
    return(
        <ul>
            {
                frds.map((frd) => (
                    <Frds frd={frd}></Frds>
                ))
            }
        </ul>
    )
}

const Frds = ({frd}) => {
    return(
        <>
            <li key={frd.id}>
                <img src={frd.image} alt={frd.name} />
                <h3>{frd.name}</h3>
                <p>{frd.balance}</p>
                <Button>Select</Button>
            </li>
        </>
    )
}

const AddFrd = () => {
    const [name,setName] = useState('');
    const [img,setImg] = useState('https://i.pravatar.cc/48');

    const handleName = (e) => {
        setName(e.target.value)
    }
    const handleImg = (e) => {
        setImg(e.target.value)
    }
    return(
        <form className="add-frd">
            <label>Friend Name :</label>
            <input type="text" onChange={handleName}/>
            <label>Image :</label>
            <input type="text" onChange={handleImg}/>
            <Button>Add</Button>
        </form>
    )
}

const SplitBill = () => {
    return(
        <form className="form-split-bill">

            <h1>SPLIT A BILL WITH MAX</h1>
            <div>
            <label>Bill value</label>
            <input type="text" />
            <label>Your Expense</label>
            <input type="text" />

            <label>Bill value</label>
            <input type="text" />

            <label>Friend Expense</label>
            <input type="text" readOnly/>
            <label>Who is paying bill</label>
            <select>
                <option>Friend1</option>
                <option>Friend2</option>
            </select> 
            </div>
            <Button>Split</Button>
        </form>
    )
}
