import React,{Children, useState} from "react";

const initial = [
    {
      id: 118836,
      name: "Clark",
      image: "https://i.pravatar.cc/48?u=118836",
      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];
export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    return(
        <div className="main">
            <div className="sidebar">
                <FriendsList frds={frds}></FriendsList>
                <Button>AddFriend</Button>
                <Addfrd></Addfrd>
            </div>
        </div>
    )
}

const Button = ({children}) => {
    return(
        <button>{children}</button>
    )
}

const FriendsList = ({frds}) => {
    return(
        <ul>
            {
            frds.map((frd)=>(
                    <Frd key={frd.id} frd={frd} />
            ))
            }
        </ul>
    )
}

const Frd = ({frd,key}) => {
    return(
        <>
            <li key={key}>
                <img src={frd.image}/>
                <h3>{frd.name}</h3>
                <p>{frd.balance}</p>
                <Button>Select</Button>
            </li>
        </>
    )
}

const Addfrd = () => {
    return(
        <>
            <label>Friend Name:</label>
            <input type="text" placeholder="Enter a name" />
            <label>Image URL : </label>
            <input type="text" placeholder="img url" />
        </>
    )
}

const BillSplit = () => {
    return(
        <>
            <form>
                <h2></h2>
            </form>
        </>
    )
}
