import React,{useState} from "react";
const initial = [
    {
      id: 118836,
      name: "Clark",
      image: "https://i.pravatar.cc/48?u=118836",
      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];


export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    return(
        <>
            <div className="app">
                <div className="sidebar">
                    <FrdsList frds={frds}></FrdsList>
                    <AddFrd></AddFrd>
                </div>
            </div>
        </>
    )
}

const FrdsList = ({frds}) => {
    return(
        <ul>
            {
                frds.map((frd) => (
                    <Frds frd={frd}></Frds>
                ))
            }
        </ul>
    )
}

const Frds = ({frd}) => {
    return(
        <>
            <li key={frd.id}>
                <img src={frd.image} alt={frd.name} />
                <h3>{frd.name}</h3>
                <p>{frd.balance}</p>
                <Button>Select</Button>
            </li>
        </>
    )
}
const Button = ({children}) => {
    return(
       <button className="button">{children}</button>
    )
}
const AddFrd = () => {
    return(
        <form>
            <label>Friend Name :</label>
            <input type="text" />
            <label>Image :</label>
            <input type="text" />
            <Button>Add</Button>
        </form>
    )
}

const SplitBill = () => {
    return(
        <form>
            <h1>SPLIT A BILL WITH MAX</h1>
            <label>Bill value</label>
            <input type="text" />
            <label>Your Expense</label>
            <input type="text" />
            <label>Bill value</label>
            <input type="text" />
            <label>Friend Expense</label>
            <input type="text" readOnly/>
            <label>Bill value</label>
            <input type="text" />
        </form>
    )
}
