import React,{Children, useState} from "react";

const initial = [
    {
      id: 118836,
      name: "Clark",
      image: "https://i.pravatar.cc/48?u=118836",
      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];
export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    const [show,setShow] = useState(true);
    const showFrd = () => {
        setShow(!show)
    }

    const handleNewFrd = (frd) => {
        setFrds((frd) => [...frd,frds])
    }
    return(
        <div className="main">
            <div className="sidebar">
                <FriendsList frds={frds}></FriendsList>
                <Button onClick={showFrd}>AddFriend</Button>
                {!show && <Addfrd></Addfrd>}
            </div>
                <BillSplit></BillSplit>
        </div>
    )
}

const Button = ({children,onClick}) => {
    return(
        <button className="btn" onClick={onClick}>{children}</button>
    )
}

const FriendsList = ({frds}) => {
    return(
        <ul>
            {
            frds.map((frd)=>(
                    <Frd key={frd.id} frd={frd} />
            ))
            }
        </ul>
    )
}

const Frd = ({frd,key}) => {
    return(
        <>
            <li key={key}>
                <img src={frd.image}/>
                <h3>{frd.name}</h3>

                {frd.balance < 0 && (
                <p className="red">
                        You owe {frd.name} {Math.abs(frd.balance)}€
                        </p>
                )}

                {frd.balance > 0 && (
                        <p className="green">
                        {frd.name} owes you {Math.abs(frd.balance)}€
                        </p>
                )}
                {frd.balance === 0 && <p>You and {frd.name} are even</p>}

                <Button>Select</Button>
            </li>
        </>
    )
}

const Addfrd = () => {
    const [name,setName] = useState('');
    const [img,setImg] = useState("https://i.pravatar.cc/48");

    const handleName = (e) => {
        setName(e.target.value);
    }
    const handleImg = (e) => {
        setImg(e.target.value);
    }
    return(
        <>
            <div>
                <label>Friend Name:</label>
                <input type="text" placeholder="Enter a name" 
                value={name} onChange={handleName}
                />
                <label>Image URL : </label>
                <input type="text" placeholder="img url" 
                value={img} onChange={handleImg}
                />
            </div>
        </>
    )
}

const BillSplit = () => {
    return(
        <>
            <form>
                <h2>Split a bill with anthony</h2>
                <label>Bill Value</label>
                <input type="text" />
                <label>Your Expense</label>
                <input type="text" />
                <label>Anthony's Expense</label>
                <input type="text" />
                <label>Who is paying the bill</label>
                <select>
                    <option>Anothony</option>
                    <option>Alex</option>
                </select>
            </form>
        </>
    )
}
