import React,{useState} from "react";
const initial = [
    {
      id: 118836,
      name: "Clark",
      image: "https://i.pravatar.cc/48?u=118836",
      balance: -7,
    },
    {
      id: 933372,
      name: "Sarah",
      image: "https://i.pravatar.cc/48?u=933372",
      balance: 20,
    },
    {
      id: 499476,
      name: "Anthony",
      image: "https://i.pravatar.cc/48?u=499476",
      balance: 0,
    },
  ];


export const Alter = () => {
    const [frds,setFrds] = useState(initial);
    const [showFrd,setShowFrd] = useState(false);
    const [selectedFriend, setSelectedFriend] = useState(null);

    const handleShowFrd = () => {
        setShowFrd(!showFrd);
    }

     const handleShowBill = () => {
        setShowBill(() => !showBill);
    } 

    const handleDelete = (id) => {
        const newFrds = frds.filter( (frd) => frd.id !== id);
        setFrds(newFrds);
    }

    function handleSelection(frd) {
        // setSelectedFriend(friend);
        setSelectedFriend((cur) => (cur?.id === friend.id ? null : friend));
        setShowAddFriend(false);
      }

    const handleAddFrd = (frd) => {
        setFrds(() => [...frds,frd]);
        console.log(frds);
    }

    return(
        <>
            <div className="app">
                <div className="sidebar">
                    <FrdsList frds={frds} showBill={handleShowBill} setFrds={setFrds}></FrdsList>
                    <Button onClick={handleShowFrd}>Add Friend</Button>
                    {showFrd && <AddFrd handleAdd={handleAddFrd}></AddFrd>}
                </div>
                {showBill && <SplitBill handleDelete={handleDelete} frds={frds}></SplitBill>}
            </div>
        </>
    )
}
const Button = ({children,onClick}) => {
    return(
       <button className="button" onClick={onClick}>{children}</button>
    )
}
const FrdsList = ({frds,showBill}) => {
    return(
        <ul>
            {
                frds.map((frd) => (
                    <Frds frd={frd} showBill={showBill} ></Frds>
                ))
            }
        </ul>
    )
}

const Frds = ({frd,showBill}) => {

    return(
        <>
            <li key={frd.id}>
                <img src={frd.image} alt={frd.name} />
                <h3>{frd.name}</h3>
                <p>{frd.balance}</p>
                <Button onClick={() => showBill()}>Select</Button>
            </li>
        </>
    )
}

const AddFrd = ({handleAdd}) => {
    const [name,setName] = useState('');
    const [img,setImg] = useState('https://avatar.iran.liara.run/public');

    const handleName = (e) => {
        setName(e.target.value)
    }
    const handleImg = (e) => {
        setImg(e.target.value)
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        const id = crypto.randomUUID();
        if(!name || !img) return;

        const newFrd = {
            id,
            name,
            image:`${img}?=${id}`
        }
        handleAdd(newFrd);
        setName('')
    }

    return(
        <form className="add-frd" onSubmit={handleAdd}>
            <label>Friend Name :</label>
            <input type="text" value={name} onChange={handleName}/>
            <label>Image :</label>
            <input type="text" value={img} onChange={handleImg}/>
            <Button onClick={handleSubmit}>Add</Button>
        </form>
    )
}

const SplitBill = ({handleDelete,frds}) => {
    console.log(frds.id)
    return(
        <form className="form-split-bill">

            <h1>SPLIT A BILL WITH MAX</h1>
            <div>
            <label>Bill value</label>
            <input type="text" />
            <label>Your Expense</label>
            <input type="text" />

            <label>Bill value</label>
            <input type="text" />

            <label>Friend Expense</label>
            <input type="text" readOnly/>
            <label>Who is paying bill</label>
            <select>
                <option>Friend1</option>
                <option>Friend2</option>
            </select> 
            </div>
            <Button>Split</Button>
        </form>
    )
}
